import {
    ADD_CONTACT,
    DELETE_CONTACT,
    SET_CURRENT,
    CLEAR_CURRENT,
    UPDATE_CONTACT,
    FILTER_CONTACTS,
    CLEAR_FILTER,
} from '../types';

export default (state, { type, payload }) => {
    switch (type) {
        case ADD_CONTACT:
            return {
                ...state,
                contacts: [...state.contacts, payload],
            };
        case DELETE_CONTACT:
            const filteredContacts = state.contacts.filter(
                contact => contact.id !== payload
            );
            return {
                ...state,
                contacts: filteredContacts,
            };
        default:
            return state;
    }
};
