import React, { useState, useContext } from 'react';

import AlertContext from '../../context/alert/alertContext';
import { validateForm } from '../../utils';

const Register = () => {
    const { setAlert } = useContext(AlertContext);
    const [user, setUser] = useState({
        name: '',
        email: '',
        password: '',
        passwordConfirm: '',
    });
    const { name, email, password, passwordConfirm } = user;

    const onChange = e => {
        setUser({ ...user, [e.target.name]: e.target.value });
    };

    const handleSubmit = e => {
        e.preventDefault();
        const { valid, msg } = validateForm(user);
        if (!valid) {
            setAlert('danger', msg);
            return;
        }
        // if (
        //     name.trim() === '' ||
        //     email.trim() === '' ||
        //     password.trim() === '' ||
        //     passwordConfirm.trim() === ''
        // ) {
        //     setAlert('danger', 'Missing required fields');
        //     return;
        // } else if (password !== passwordConfirm) {
        //     setAlert('danger', 'Password fields do not match');
        //     return;
        // } else if (password.length < 6) {
        //     setAlert('danger', 'Password must be at least 6 characters');
        //     return;
        // }
        console.log('USER', user);
    };

    return (
        <div className='form-container'>
            <h1 className='text-center'>
                Account
                <span className='text-primary pl'>Register</span>
            </h1>
            <form>
                <div className='form-group'>
                    <label htmlFor='name'>Name</label>
                    <input
                        type='text'
                        name='name'
                        value={name}
                        onChange={onChange}
                        required
                    />
                </div>
                <div className='form-group'>
                    <label htmlFor='email'>Email Address</label>
                    <input
                        type='email'
                        name='email'
                        value={email}
                        onChange={onChange}
                        required
                    />
                </div>
                <div className='form-group'>
                    <label htmlFor='password'>Password</label>
                    <input
                        type='password'
                        name='password'
                        value={password}
                        onChange={onChange}
                        required
                        minLength='6'
                    />
                </div>
                <div className='form-group'>
                    <label htmlFor='passwordConfirm'>Confirm Password</label>
                    <input
                        type='password'
                        name='passwordConfirm'
                        value={passwordConfirm}
                        onChange={onChange}
                        required
                        minLength='6'
                    />
                </div>
                <button
                    onClick={handleSubmit}
                    className='btn btn-primary btn-block'
                >
                    Register
                </button>
            </form>
        </div>
    );
};

export default Register;
